import React, { Component } from 'react'
import './index.scss'
import http from './../../api/index'
import { Input,Radio } from 'antd';

export default class Home extends Component {
    state={
        num:'',
        data:[]
    }
    // constructor(props:Object){
    //     super(props)
    // }
    componentDidMount(){
      // console.log(http)
    }
    onChange=(e:any)=>{
        const str = e.target.value
        this.setState(()=>{
            return {num:str}
        })
      }
      handleSizeChange(){
        console.log('event.target')
      }
      onBlur=()=>{
        http.searchSongsByName(this.state.num).then((res:any)=>{
          console.log(res)
          if (res.status===200) {
            const {data}= res
            console.log(JSON.parse(data.body))
            this.setState(()=>{
              return {data:JSON.parse(data.body).data.album.list}
            })
            console.log(this.state.data)
          } else {

          }
        })

    }
   render(){
     return (<div>
      <div className="search-input">
        <Input size="default" value={this.state.num} placeholder="请输入歌曲名称" 
        onChange={this.onChange.bind(this)} onBlur={this.onBlur.bind(this)} />
          <Radio.Group value={'default'} onChange={this.handleSizeChange}>
          <Radio.Button value="large">Large</Radio.Button>
          <Radio.Button value="default">Default</Radio.Button>
          <Radio.Button value="small">Small</Radio.Button>
        </Radio.Group>
      </div>
      <ul className="ul">
        {this.state.data.map( (item:any) => <li key={item.albumID}>
          <img src={item.albumPic} alt="图片不存在"/>
          <span>{item.albumName}</span>
          <span>{item.singerName}</span>
        </li> )}
      </ul>
     </div>)
  }
}
