// })
export default {
    getSongs(id:String) {
      return axios({
        method: 'get',
        url: '/cloudmusic',
        params: {
          type: 'detail',
          id
        }
      })
    },
    searchSongsByName(songsName:String) {
      return axios({
        method: 'get',
        url: '/songs',
        params: {
          songsName
        }
      })
    },
    searchSongsBySinger(singer:String) {
      return axios({
        method: 'get',
        url: '/singer',
        params: {
          singer
        }
      })
    },
    comments(id:Number) {
      return axios({
        method: 'get',
        url: '/cloudmusic',
        params: {
          type: 'comments',
          id
        }
      })
    }
  }
