import * as React from 'react'
import http from './../../api'
import './index.css'
import { Comment, Tooltip, List } from 'antd';
import moment from 'moment';

export default class About extends React.Component<any> {
    state={
      params:{
        g_tk:5381,
        loginUin:0,
        hostUin:0,
        format:'json',
        inCharset:'utf8',
        outCharset:'GB2312',
        notice:0,
        platform:'yqq.json',
        needNewCode:0,
        cid:205360772,
        reqtype:2,
        biztype:1,
        topid:0,
        cmd:8,
        needmusiccrit:0,
        pagenum:0,
        pagesize:25,
        lasthotcommentid:'',
        domain:'qq.com',
        ct:24,
        cv:10101010
      },
    }
    componentDidMount(){
      var params = Object.assign({},this.state.params,{topid:this.props.match.params['id']})
      this.setState(()=>{
        return {params}
      })
     setTimeout(() => {
      this.getComment()
     }, 0);
    }
    getComment() {
      http.getComment(this.state.params,this.props.match.params['mid']).then((res:any)=>{
        console.log(JSON.parse(res.body)['hot_comment'].commentlist)
        const arr = JSON.parse(res.body)['hot_comment'].commentlist.map((item:any)=>{
              return   {
                  actions: [<span key="comment-list-reply-to-0">Reply to</span>],
                  author: 'Han Solo',
                  avatar: item.avatarurl,
                  content: (
                    <p>
                      We supply a series of design principles, practical patterns and high quality design
                      resources (Sketch and Axure), to help people create their product prototypes beautifully and
                      efficiently.
                    </p>
                  ),
                  datetime: (
                    <Tooltip
                      title={moment()
                        .subtract(1, 'days')
                        .format('YYYY-MM-DD HH:mm:ss')}
                    >
                      <span>
                        {moment()
                          .subtract(1, 'days')
                          .fromNow()}
                      </span>
                    </Tooltip>
                  ),
                }
        })
      })
    }
   render(){
     return (<div className="about">
      {/* <List
        className="comment-list"
