import axios from 'axios'
interface requestTypes {
  data:object,
  status:number,
  statusText:string,
  request:object,
  config:object,
  headers:object
  // color?: string,
  // width?: number,
}
axios.interceptors.response.use(function(params:requestTypes):Promise<any> {
  console.log(params)
  return params
})
export default {
    getSongs(id:String) {
      return axios({
        method: 'get',
        url: '/cloudmusic',
        params: {
          type: 'detail',
          id
        }
      })
    },
    //aggr=1&cr=1&flag_qc=0&p=1&n=30&w=%E9%BE%99%E5%8D%B7%E9%A3%8E
