import axios from 'axios'
export default {
    getSongs(id:String) {
      return axios({
        method: 'get',
        url: '/cloudmusic',
        params: {
          type: 'detail',
          id
        }
      })
    },
    //aggr=1&cr=1&flag_qc=0&p=1&n=30&w=%E9%BE%99%E5%8D%B7%E9%A3%8E
    searchSongsByName(id:String) {
      return axios({
        method: 'get',
        url: '/api/soso/fcgi-bin/client_search_cp',
        params: {
          type: 'detail',
          id
        }
      })
    },
    comments(id:Number) {
      return axios({
        method: 'get',
        url: '/cloudmusic',
        params: {
          type: 'comments',
          id
        }
      })
    }
  }
